import { useState } from 'react';
import './CampoTexto.css'



const CampoTexto = (props) => {

    const placeHolderAtualizada = `${props.placeholder} ...`;
    //let valor = 'Felipe Bianchine';

    const [valor, setValor] = useState('Felipe Bianchine')

    const aoDigitado = (evento) => {
        setValor(evento.target.value);
        console.log(valor);
    }
    return (
        
        <div className="campo-texto">
            <label>
                {props.label}
            </label>
            <input value={valor} onChange={aoDigitado} required={props.obrigatorio} placeholder={placeHolderAtualizada} />
        </div>
    )



}

export default CampoTexto;
